mapper.select:
select
  id, url, hash, remark, create_time, update_time
from url_blacklist


        and id = #{id}


        and url = #{url}


        and hash = #{hash}


        and remark = #{remark}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from url_blacklist


        and id = #{id}


        and url = #{url}


        and hash = #{hash}


        and remark = #{remark}


        and create_time = #{createTime}


        and update_time = #{updateTime}
